SELECT "Motos_2"."Anunciante (grupo)" AS "Anunciante (grupo)",   "Motos_2"."Marca" AS "Datos (copia)",   "Motos_2"."Medio" AS "Medio",   "Motos_2"."PrimeraLinea" AS "PrimeraLinea",   "Motos_2"."VER ANUNCIO" AS "VER ANUNCIO",   "Motos_2"."Vehiculo" AS "Vehiculo",   CAST(EXTRACT(MONTH FROM "Motos_2"."FECHA") AS BIGINT) AS "mn:FECHA:ok",   SUM(CAST("Motos_2"."NumAnuncios" AS BIGINT)) AS "sum:NumAnuncios:ok",   CAST(EXTRACT(YEAR FROM "Motos_2"."FECHA") AS BIGINT) AS "yr:FECHA:ok" FROM "Motos_2" WHERE ((CAST(EXTRACT(YEAR FROM "Motos_2"."FECHA") AS BIGINT) >= 2010) AND (CAST(EXTRACT(YEAR FROM "Motos_2"."FECHA") AS BIGINT) <= 2015) AND ('Cerrar' = 'Cerrar') AND (CAST(EXTRACT(YEAR FROM "Motos_2"."FECHA") AS BIGINT) = 2015) AND (CAST(EXTRACT(MONTH FROM "Motos_2"."FECHA") AS BIGINT) IN (9, 10, 11)) AND ("Motos_2"."Categoria" = 'MOTOCICLETAS')) GROUP BY 1,   2,   3,   4,   5,   6,   7,   9;
