--SELECT AVG(CAST("Redfin4_1"."price_drops" AS double)) AS "avg:price_drops:ok",   "Redfin4_1"."region" AS "region",   TABLEAU.TO_DATETIME(DATE_TRUNC('MONTH', TABLEAU.NORMALIZE_DATETIME("Redfin4_1"."period_end")), "Redfin4_1"."period_end") AS "tmn:period_end:ok" FROM "Redfin4_1" WHERE (("Redfin4_1"."region" = 'Westminster, CA') AND (TABLEAU.NORMALIZE_DATETIME(TABLEAU.TO_DATETIME(DATE_TRUNC('MONTH', TABLEAU.NORMALIZE_DATETIME("Redfin4_1"."period_end")), "Redfin4_1"."period_end")) >= cast('2014-11-01' as DATE)) AND (TABLEAU.NORMALIZE_DATETIME(TABLEAU.TO_DATETIME(DATE_TRUNC('MONTH', TABLEAU.NORMALIZE_DATETIME("Redfin4_1"."period_end")), "Redfin4_1"."period_end")) <= cast('2016-06-01' as DATE)) AND ("Redfin4_1"."property_type" NOT IN ('All Residential', 'Multi-Family (2-4 Unit)'))) GROUP BY 2,   3;
