SELECT "USCensus_1"."Agep (bin)" AS "Agep (bin)",   CAST("USCensus_1"."REGION" AS BIGINT) AS "REGION",   CAST("USCensus_1"."SCHL" AS TEXT) AS "SCHL",   SUM(CAST("USCensus_1"."Number of Records" AS BIGINT)) AS "sum:Number of Records:ok" FROM "USCensus_1" GROUP BY 1,   "USCensus_1"."REGION",   3,   2;
