SELECT "USCensus_3"."Agep (bin)" AS "Agep (bin)",   CAST("USCensus_3"."REGION" AS BIGINT) AS "REGION",   CAST("USCensus_3"."SCHL" AS TEXT) AS "SCHL",   SUM(CAST("USCensus_3"."Number of Records" AS BIGINT)) AS "sum:Number of Records:ok" FROM "USCensus_3" GROUP BY 1,   "USCensus_3"."REGION",   3,   2;
